UI.css('*{margin-top:2%;background-color:black;text-align:center;}');
const canvas = UI.create('canvas');
(()=>{
  canvas.width = 500;
  canvas.height = 500;
  const ctx = canvas.getContext('2d');
  const r = 4;
  ctx.fillStyle = 'black';
  const line = { x: 0, y: 0 };
  let time = 0;

  ctx.fillRect(0, 0, canvas.width, canvas.height);
  ctx.translate(canvas.width / 2, canvas.height / 2);

  function update() {
    const color = 0.15*Math.sin((time)/2);
    ctx.fillStyle = 'rgba(0, 0, 0,'+ color +')';
    ctx.fillRect(-canvas.width / 2, -canvas.height / 2, canvas.width, canvas.height);
    line.x =  r * 16 * Math.pow(Math.sin(time), 3); 
    line.y = -r * (13 * Math.cos(time) - 5 * Math.cos(2 * time) - 2 * Math.cos(3 * time) - Math.cos(4 * time));

    ctx.beginPath();
    ctx.fillStyle = '#d40824';
    ctx.ellipse(line.x, line.y, 2, 2, Math.PI / 4, 0, 2 * Math.PI);
    ctx.fill();
    time += 1 / 30;
   requestAnimationFrame(update);
  }
update();
})();
